import { useFormik } from 'formik'
import * as Yup from 'yup'
import dayjs from 'dayjs'

const cdate = new Date();
let firstDay = new Date(cdate.getFullYear(), cdate.getMonth(), 1);
let lastDay = new Date(cdate.getFullYear(), cdate.getMonth() + 1, 0);
firstDay = dayjs(firstDay).format('YYYY-MM-DD')
lastDay = dayjs(lastDay).format('YYYY-MM-DD')
const MensalidadesResumo = () => {

  const { handleSubmit, handleChange, values, touched, errors } = useFormik({
    initialValues: {
      idate: firstDay,
      fdate: lastDay
    },
    validationSchema: Yup.object({
      idate: Yup.date().required('Obrigatório'),
      fdate: Yup.date().required('Obrigatório')
    }),
    onSubmit: (values) => {
      salvarAluno(values, props.user.email)
    }
  })
  return (
    <div>
      <h3>Mensalidades</h3>
      <p>Resumo das Mensalidades</p>
      <input
        value={values.idate}
        onChange={handleChange}
        type="date"
        id="idate"
        name="idate"
      />
      {touched.idate && errors.idate ? <text>{errors.idate}</text> : null}
      <input
        value={values.fdate}
        onChange={handleChange}
        type="date"
        id="fdate"
        name="fdate"
      />
      {touched.fdate && errors.fdate ? <text>{errors.fdate}</text> : null}
      <input type="submit" value="OK"></input>
    </div>
  )
}

export default MensalidadesResumo

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req)
  if (session) {
    const data = await fetch('http://localhost:3001/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        Authorization:
          'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJjbGllbnQiOiJmcm9udC1lbmQtS2U0Z3JnNzRocjRkY2I2IiwiaWF0IjoxNTk3ODU3NTg2fQ.MVQIQxXkVgbhCFzYRwIiAtJZHbYN0UqiJGBndMLKAGY',
      },
      body: JSON.stringify({
        query: `{ 
          findMensalidadesByPeriod(user: "${session.user.email}", input: {
            idate: ${firstDay}
            fdate: ${lastDay}
          }) {
            price
        } }`,
      }),
    })
    const alunosDB = await data.json()
    const alunos = alunosDB.data
    let errors = null
    if (alunosDB.errors) {
      errors = alunosDB.errors
    }

    return {
      props: {
        errors,
        user: session.user,
        data: alunos,
      },
    }
  }
  return {
    props: {
      user: 'Usuário não logado',
      data: 'Dados inacessíveis'
    }
  }
}
