import React from 'react'
import auth0 from '../lib/auth0'

const App = props => {
  return <pre>{JSON.stringify(props, null, 2)}</pre>
}

export default App

export async function getSideServerProps({ req, res }) {
  const session = auth0.getSession(req)
  if (session) {
    return {
      props: {
        user: session.user
      }
    }
  } else {
    return {
      props: {
        user: 'Usuário não logado'
      }
    }
  }
}
