import { useFormik } from 'formik'
import * as Yup from 'yup'
import dayjs from 'dayjs'

const cdate = new Date();
let firstDay = new Date(cdate.getFullYear(), cdate.getMonth(), 1);
let lastDay = new Date(cdate.getFullYear(), cdate.getMonth() + 1, 0);
firstDay = dayjs(firstDay).format('YYYY-MM-DD')
lastDay = dayjs(lastDay).format('YYYY-MM-DD')

const MensalidadesResumo = ({ email }) => {

  const { handleSubmit, handleChange, values, touched, errors } = useFormik({
    initialValues: {
      idate: firstDay,
      fdate: lastDay
    },
    validationSchema: Yup.object({
      idate: Yup.date().required('Obrigatório'),
      fdate: Yup.date().required('Obrigatório')
    }),
    onSubmit: (values) => {
      buscarMensalidade(email, values)
    }
  })
  return (
    <div>
      <h3>Mensalidades</h3>
      <p>Resumo das Mensalidades</p>
      <input
        value={values.idate}
        onChange={handleChange}
        type="date"
        id="idate"
        name="idate"
      />
      {touched.idate && errors.idate ? <text>{errors.idate}</text> : null}
      <input
        value={values.fdate}
        onChange={handleChange}
        type="date"
        id="fdate"
        name="fdate"
      />
      {touched.fdate && errors.fdate ? <text>{errors.fdate}</text> : null}
      <input type="submit" value="OK"></input>
    </div>
  )
}

export default MensalidadesResumo

const buscarMensalidade = async (email, values) => {
  const data = await fetch('http://localhost:3001/graphql', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Accept: 'application/json',
      Authorization:
        'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJjbGllbnQiOiJmcm9udC1lbmQtS2U0Z3JnNzRocjRkY2I2IiwiaWF0IjoxNTk3ODU3NTg2fQ.MVQIQxXkVgbhCFzYRwIiAtJZHbYN0UqiJGBndMLKAGY',
    },
    body: JSON.stringify({
      query: `{ 
          findMensalidadesByPeriod(user: "${email}", input: {
            idate: ${values.idate}
            fdate: ${values.fdate}
          }) {
            price
        } }`,
    }),
  })
  const mensalidadesDB = await data.json()
  const mensalidades = mensalidadesDB.data
  let errors = null
  if (mensalidadesDB.errors) {
    errors = mensalidadesDB.errors
  }

  return {
    props: {
      errors,
      data: mensalidades
    }
  }

  return {
    props: {
      user: 'Usuário não logado',
      data: 'Dados inacessíveis'
    }
  }
}
