import React from 'react'
import auth0 from '../lib/auth0'

const App = props => {
  return (
    <div>
      <h1>App</h1>
      <pre>{JSON.stringify(props, null, 2)}</pre>
    </div>
  )
}

export default App

export async function getSideServerProps({ req, res }) {
  const session = await auth0.getSession(req)
  console.log(session)
  if (session) {
    return {
      props: {
        user: session.user
      }
    }
  }
  return {
    props: {
      user: 'Usuário não logado'
    }
  }
}
